  $body-font-size: 1rem;
  $body-font-weight: 400;
  --body-font-size: #{$body-font-size};
  --body-font-weight: #{$body-font-weight};

  //Labels
  $label-font-size: 1.1rem;
  $label-font-weight: 700;
  --label-font-size: #{$label-font-size};
  --label-font-weight: #{$label-font-weight};

