  $h3-font-weight: 600;
  --h3-font-size: #{$h3-font-size};
  --h3-font-weight: #{$h3-font-weight};

  //Restaurant review rating colors
  --no-rating: gray;
  --high-rating: green;
  --mid-rating: yellow;
  --low-rating: red;

  //Report Status colors
  --active-status-color: #46a854;
  --closed-status-color: #a84846;

  //App header
  --app-header-height: 60px;
  --app-header-txt-color: #fff;
  --app-header-title-font-size: 1.6em;

  //Shoelace tokens
  --sl-color-primary-50: #6f7a82;
  --sl-color-primary-100: #{lighten($brand-color, 40%)};
  --sl-color-primary-200: #{lighten($brand-color, 30%)};
  --sl-color-primary-300: #{lighten($brand-color, 20%)};
  --sl-color-primary-400: #{lighten($brand-color, 10%)};
  --sl-color-primary-500: #{$brand-color};
