/**
 * Header component
 * 
 * Author: Ryan Riddiford
 * Student ID: 20862086
 */


//Import dependencies
import { LitElement, html, css } from '@polymer/lit-element';
import {anchorRoute, gotoRoute} from '../../Router';
import Auth from '../../services/AuthAPI';
import App from '../../App';


//Define the header element as a custom element
customElements.define('top-header', class AppHeader extends LitElement {


  constructor(){
    super()    
  }

  //Configure the element's custom properties
  static get properties(){
    return {
      title: {
        type: String
      },
      user: {
        type: Object
      },
      logoUrl: {
        type: String,
        default: "../static/logo.png"
      }
    }
  }


  firstUpdated(){
    super.firstUpdated()
    this.navActiveLinks()    
  }

  navActiveLinks(){	
    const currentPath = window.location.pathname;
    const navLinks = this.shadowRoot.querySelectorAll('.app-top-nav a, .app-side-menu-items a');
    navLinks.forEach(navLink => {
      if(navLink.href.slice(-1) == '#') return;
      if(navLink.pathname === currentPath){			
        navLink.classList.add('active');
      }
    });
  }
  
  menuClick(e){
    e.preventDefault()
    const pathname = e.target.closest('a').pathname
    const appSideMenu = this.shadowRoot.querySelector('.app-side-menu')
    // hide appMenu
    appSideMenu.hide()
    appSideMenu.addEventListener('sl-after-hide', () => {
      // goto route after menu is hidden
      gotoRoute(pathname)
    })
  }

  /**
   * Renders the top header component
   * @returns The top header component
   */
  render(){  

    //Stores the available header links
    let headerLinks;

          //Setup reviewer and restaurant level navigation
    if (this.user.accessLevel == 1 || this.user.accessLevel == 2) {
headerLinks = html`
<a href="/search" @click="${anchorRoute}">Search</a> 
<a href="/reviews" @click="${anchorRoute}">Your Reviews</a> 
<a href="/profile" @click="${anchorRoute}">Your Profile</a> 
`;
    }
    //Setup admin level navigation
    else if (this.user.accessLevel == 3) {
      headerLinks = html`
      <a href="/search" @click="${anchorRoute}">Search</a> 
      <a href="/tickets" @click="${anchorRoute}">Active Tickets</a> 
      <a href="/history" @click="${anchorRoute}">Ticket History</a> 
      <a href="/profile" @click="${anchorRoute}">Your Profile</a> 
      `;
    }


    //Return the header component
    return html`

    <style>
    
    .app-header {
      width:100%;
      height: 250px;
      background: gray;
    }


    </style>

    <header class="app-header">
    ${localStorage.getItem('accessToken') ? 
    html` 
    <a class="header-logo" href="/" @click="${anchorRoute}"><img width="100" height="100" src="logo.png"></a>
        <nav class="app-top-nav">
${headerLinks}
    </nav>
    <a class="signout-link" href="#" @click="${() => Auth.signOut()}">Sign Out</a>` :
    html`<img width="100" height="100" src="logo.png">`
  }  
</header>
    `
  }
})
