/**
 * Login form component
 * 
 * Author: Ryan Riddiford
 * Student ID: 20862086
 */


//Import dependencies
import { LitElement, html, css } from '@polymer/lit-element';
import {anchorRoute, gotoRoute} from './../../Router';
import Auth from '../../services/AuthAPI';
import App from './../../App';


//Define the header element as a custom element
customElements.define('register-form', class RegisterForm extends LitElement {


  constructor(){
    console.log("constructing register form");
    super()    
  }

    /**
   * Send form data for signing in
   * @param {*} e The event object
   */
    registerSubmitHandler(e){
        e.preventDefault();
        const formData = e.detail.formData;
        console.log(formData);
        const submitBtn = document.querySelector('.submit-btn');
        submitBtn.setAttribute('loading', '');
        console.log("signing up");
        let endpoint = '';

//         let accessLevelInput = this.shadowRoot.querySelector('#access-level-input');

//         if (accessLevelInput == 1)
// endpoint = "create-reviewer";
// else if (accessLevelInput == 2)
// endpoint = "create-restaurant"; 



        // sign in using Auth    
        Auth.signUp(formData, () => {
          submitBtn.removeAttribute('loading')
        })
      }

  //Configure the element's custom properties
  static get properties(){
    console.log("setting properties");
    return {
      title: {
        type: String
      },
      submitBtn: {
type: HTMLElement
      },
      reviewerFields: {
type:HTMLElement
      },
      restaurantFields: {
type: HTMLElement
      },
      isReviewer: {
        type: Boolean,
        default: true
      }
    }
  }

    firstUpdated(){
    super.firstUpdated();
  }

  
  /**
   * Send form data for signing in
   * @param {*} e The event object
   */
  registerSubmitHandler(e){
    console.log("inside handler");
    e.preventDefault();
    const formData = e.detail.formData;
    console.log(formData);
    //const submitBtn = document.querySelector('#register-submit-btn')
    console.log(this.submitBtn);
    this.submitBtn.setAttribute('loading', '');   
    
    // sign in using Auth    
    Auth.signUp(formData, () => {
      this.submitBtn.removeAttribute('loading');
    })
  }


  /**
   * Switch between fields for reviewer and restaurant accounts
   * @param {*} e 
   */
  updateFormFields(e) {
    console.log("updating form fields");

    let revFields = this.shadowRoot.querySelector('#reviewer-fields');
    let resFields = this.shadowRoot.querySelector('#restaurant-fields');
    let accessLevelInput = this.shadowRoot.querySelector('#access-level-input');

    //Toggle account-specific fields
revFields.classList.toggle('hidden');
resFields.classList.toggle('hidden');

//If restaurant fields are enabled
if (revFields.classList.contains('hidden')) {
accessLevelInput.value = 2;
}
//If review fields are enabled
else if (resFields.classList.contains('hidden')) {
  accessLevelInput.value = 1;
  }
  }


  /**
   * Renders the register form html
   * @returns Render of register form
   */
  render(){  
  this.submitBtn = document.createElement('sl-button');
  this.submitBtn.classList.add('submit-btn');
  this.submitBtn.id = "";
  this.submitBtn.type = "primary";
  this.submitBtn.setAttribute("submit", "");
 this.submitBtn.innerHTML = "Register";
  //TODO
  this.submitBtn.style.width = "100%";

  //Reviewer fields
  this.reviewerFields = html`

  <div id="reviewer-fields">
  <div class="input-group">
<sl-input name="firstName" type="text" placeholder="First Name"></sl-input>
</div>
<div class="input-group">
<sl-input name="lastName" type="text" placeholder="Surname"></sl-input>
</div>
<div class="input-group">
<sl-input name="username" type="text" placeholder="Username"></sl-input>
</div>
<!-- AVATAR ID MISSING TODO -->
  </div>
  `;


      this.restaurantFields = html`
      <div id="restaurant-fields" class="hidden">
      <div class="input-group">
        <sl-input name="restaurantName" type="text" placeholder="Restaurant Name"></sl-input>
</div>
        <div class="input-group">
        <sl-input name="phoneNumber" type="text" placeholder="Phone Number"></sl-input>
</div>
<div class="input-group">
        <sl-input name="location" type="text" placeholder="Location"></sl-input>
</div>
<div class="input-group">
        <sl-input name="established" type="text" placeholder="Established"></sl-input>
</div>
<div class="input-group">
        <sl-input name="owner" type="text" placeholder="Owner"></sl-input>
</div>
<div class="input-group">
        <sl-input name="cuisine" type="text" placeholder="Cuisine"></sl-input>
</div>
      </div>`;



return html`

<style>

.hidden {
    visibility: hidden;
    display:none;
}

</style>





<sl-form class="form-signup dark-theme" @sl-submit=${this.registerSubmitHandler}>  


<sl-switch checked @sl-change="${this.updateFormFields}">Reviewer</sl-switch>


<div class="input-group">
              <sl-input id="access-level-input" name="accessLevel" type="hidden" value="1" required></sl-input>
            </div>

            <div class="input-group">
              <label>Avatar</label><br>          
                <input type="file" name="avatar" />
            </div>

            <div class="input-group">
              <sl-input name="email" type="email" value="nick@reviewer.com.au" placeholder="Email" required></sl-input>
            </div>

            <div class="input-group">
              <sl-input name="password" type="password" value="reviewer123" placeholder="Password" required toggle-password></sl-input>
            </div>

<div class="input-group">
<sl-textarea rows="2" resize="none" name="bio" type="text" placeholder="Bio" required></sl-textarea>
</div>



  ${this.reviewerFields}
  ${this.restaurantFields}


${this.submitBtn}

          
          </sl-form>`
  }
});
